'use strict';

(function (window) {
    var flag = true;
    window.methodCover = {
        capacity: function capacity(msg, time) {
            var time = time || 1000;
            var $el = $('<div class="alert-light-box"><div class="msg-box">' + msg + '</div><div class="bg-box">' + msg + '</div></div>');
            $(document.body).append($el);
            var msg = $el.find('.msg-box');
            var bg = $el.find('.bg-box');
            var leftValue = ($el.width() - (msg.width() + 60)) / 2;
            var topValue = ($el.height() - (msg.height() + 30)) / 2;
            msg.css({
                'left': leftValue + 'px',
                'top': topValue + 'px'
            });
            bg.css({
                'left': leftValue + 'px',
                'top': topValue + 'px'
            });
            setTimeout(function () {
                setTimeout(function () {
                    $el.remove();
                    $el = null;
                }, 400);
                $el.css('opacity', 0);
            }, time);
        },
        getParams: function getParams(getName) {
            var data = JSON.parse(sessionStorage.getItem(getName));
            return data;
        },
        go: function go(url, setNmae, param) {
            //跳转
            var btn = $('<a href="' + url + '" style="display:none"></a>');
            $(document.body).append(btn);
            btn.get(0).click();
            if (param && setNmae) {
                var params = JSON.stringify(param);
                sessionStorage.setItem(setNmae, params);
            };
            btn.remove();
        },
        back: function back() {
            //返回
            if (flag) {
                flag = false;
                setTimeout(function () {
                    flag = true;
                }, 700);
                window.history.back();
            }
        }
    };
})(window);(function () {
    var _LoadingHtml = '<div id="loading"><div class="cont"><div class="loadyuan"><span></span></div>加载中，请稍后</div></div>';
    document.write(_LoadingHtml);
    document.getElementById('loading').ontouchstart = function (event) {
        event.preventDefault();
    };
    function completeLoading() {
        var loadingMask = document.getElementById('loading');
        loadingMask.parentNode.removeChild(loadingMask);
    }
    if (document.readyState == "complete") {
        completeLoading();
    } else {
        document.onreadystatechange = function () {
            if (document.readyState == "complete") {
                completeLoading();
            }
        };
    }
})();

